package com.fr.domain;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.fr.utils.CustomDateDeserializer;import com.fr.utils.CustomDateSerializer;import javax.validation.constraints.Max;import javax.validation.constraints.Min;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.util.Date;/** * Created on 19/05/2017 * * @author Ming Li */public class Rating {    @NotNull    private String userId;    @Min(1)    @Max(10)    private int rate;    @JsonDeserialize(using = CustomDateDeserializer.class)    @JsonSerialize(using = CustomDateSerializer.class)    private Date timestamp;    @Size(max = 200)    private String body;    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }    public int getRate() {        return rate;    }    public void setRate(int rate) {        this.rate = rate;    }    public Date getTimestamp() {        return timestamp;    }    public void setTimestamp(Date timestamp) {        this.timestamp = timestamp;    }    public String getBody() {        return body;    }    public void setBody(String body) {        this.body = body;    }}